import React, { useState, useEffect, useRef } from "react";
import "./App.css";

// Helper function to determine if a sentence should be considered a question.
function isQuestionSentence(sentence) {
  sentence = sentence.trim();
  if (sentence.length === 0) return false;

  // Split the sentence into words.
  const words = sentence.split(/\s+/);
  const firstWord = words[0].toLowerCase();

  // Words that indicate a question only if they appear at the beginning.
  const startOnlyIndicators = [
    "is", "are", "am", "was", "were", "do", "does", "did",
    "can", "could", "would", "should", "will", "shall"
  ];
  // Words that may indicate a question regardless of position.
  const generalIndicators = ["what", "why", "how", "when", "where", "who"];

  // Check the first word.
  if (startOnlyIndicators.includes(firstWord) || generalIndicators.includes(firstWord)) {
    return true;
  }

  // If the sentence has at least 4 words, also check the second word (for start-only indicators).
  if (words.length >= 4) {
    const secondWord = words[1].toLowerCase();
    if (startOnlyIndicators.includes(secondWord)) {
      return true;
    }
  }

  // Also check if any general indicator appears later.
  for (let word of words.slice(1)) {
    if (generalIndicators.includes(word.toLowerCase())) {
      return true;
    }
  }

  return false;
}

// Helper function to format a sentence by trimming, capitalizing the first letter,
// and appending appropriate punctuation if missing.
function formatSentence(sentence) {
  sentence = sentence.trim();
  if (sentence.length === 0) return "";

  const isQuestion = isQuestionSentence(sentence);
  // Capitalize the first letter.
  sentence = sentence.charAt(0).toUpperCase() + sentence.slice(1);

  // Append punctuation if missing.
  if (!/[.!?]$/.test(sentence)) {
    sentence += isQuestion ? "?" : ".";
  }

  return sentence;
}

// Set up the Web Speech API.
const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
const recognition = new SpeechRecognition();
recognition.continuous = true;
recognition.interimResults = true;
recognition.lang = "en-US";

function App() {
  // State for managing recording, transcript data, and status messages.
  const [isRecording, setIsRecording] = useState(false);
  const [finalTranscript, setFinalTranscript] = useState("");
  const [interimTranscript, setInterimTranscript] = useState("");
  const [statusMessage, setStatusMessage] = useState("");

  // Ref to the text container (for auto-scrolling).
  const containerRef = useRef(null);

  // State to hold the WebSocket instance (or null if unavailable).
  const [socket, setSocket] = useState(null);

  // Establish and manage the WebSocket connection.
  useEffect(() => {
    let ws = null;
    try {
      // Replace with your actual WebSocket server URL (e.g., "ws://localhost:8080")
      ws = new WebSocket("ws://yourserveraddress:port");
      ws.onopen = () => {
        setStatusMessage("Connected to WebSocket server");
      };
      ws.onerror = (error) => {
        setStatusMessage("WebSocket error: " + error.message);
        setSocket(null);
      };
      setSocket(ws);
    } catch (error) {
      setStatusMessage("WebSocket connection error: " + error.toString());
      setSocket(null);
    }
    return () => {
      if (ws) ws.close();
    };
  }, []);

  // Set up speech recognition event handlers.
  useEffect(() => {
    recognition.onresult = (event) => {
      let final = "";
      let interim = "";
      // Process recognized results.
      for (let i = event.resultIndex; i < event.results.length; i++) {
        const transcript = event.results[i][0].transcript;
        if (event.results[i].isFinal) {
          final += transcript;
        } else {
          interim += transcript;
        }
      }
      // Format and append finalized transcript.
      if (final) {
        const formattedFinal = formatSentence(final);
        setFinalTranscript(prev => prev + " " + formattedFinal);
      }
      // Update interim transcript for live display.
      setInterimTranscript(interim);
    };

    recognition.onerror = (event) => {
      setStatusMessage("Speech recognition error: " + event.error);
    };

    recognition.onend = () => {
      setStatusMessage("Speech recognition ended.");
      if (isRecording) {
        setStatusMessage("Restarting speech recognition...");
        recognition.start();
      }
    };
  }, [isRecording]);

  // Periodically refresh the speech recognition session (e.g., every 50 seconds).
  useEffect(() => {
    if (!isRecording) return;
    const refreshInterval = 50000;
    const intervalID = setInterval(() => {
      setStatusMessage("Refreshing recognition session...");
      recognition.stop();
    }, refreshInterval);
    return () => clearInterval(intervalID);
  }, [isRecording]);

  // Auto-scroll the text container.
  useEffect(() => {
    if (containerRef.current) {
      containerRef.current.scrollTo({
        top: containerRef.current.scrollHeight,
        behavior: "smooth",
      });
    }
  }, [finalTranscript, interimTranscript]);

  // Send finalized transcript via WebSocket.
  useEffect(() => {
    if (socket && socket.readyState === WebSocket.OPEN) {
      try {
        const message = JSON.stringify({ finalTranscript });
        socket.send(message);
        setStatusMessage("Sent finalized transcript: " + message);
      } catch (err) {
        setStatusMessage("Failed to send message: " + err.toString());
      }
    } else {
      setStatusMessage("WebSocket not available. Running without sending transcript.");
    }
  }, [finalTranscript, socket]);

  // Toggle recording.
  const toggleRecording = () => {
    if (isRecording) {
      recognition.stop();
    } else {
      setInterimTranscript("");
      recognition.start();
    }
    setIsRecording(!isRecording);
  };

  return (
    <div className="App">
      <button className="mic-button" onClick={toggleRecording}>
        {isRecording ? "Stop" : "Record"}
      </button>
      {/* Display the status message next to the record button */}
      <div className="status-message">{statusMessage}</div>
      <div className="text-container" ref={containerRef}>
        <p className="transcript">
          {finalTranscript} <span className="interim">{interimTranscript}</span>
        </p>
      </div>
    </div>
  );
}

export default App;
