import React, { useState, useEffect, useRef } from "react";
import "./App.css";

// Set up the Web Speech API for speech recognition
const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
const recognition = new SpeechRecognition();
// Configure the speech recognition instance for continuous listening and to capture interim results
recognition.continuous = true;
recognition.interimResults = true;
recognition.lang = "en-US";

function App() {
  // State for tracking whether recording is active
  const [isRecording, setIsRecording] = useState(false);
  // State to store finalized transcript text
  const [finalTranscript, setFinalTranscript] = useState("");
  // State to store the live interim transcript text
  const [interimTranscript, setInterimTranscript] = useState("");
  // Reference to the scrollable text container for auto-scrolling functionality
  const containerRef = useRef(null);

  // Initialize speech recognition event handlers once on component mount
  useEffect(() => {
    recognition.onresult = (event) => {
      let final = "";
      let interim = "";
      // Loop through the results to separate finalized from interim text
      for (let i = event.resultIndex; i < event.results.length; i++) {
        const transcript = event.results[i][0].transcript;
        if (event.results[i].isFinal) {
          final += transcript;
        } else {
          interim += transcript;
        }
      }
      // Append finalized transcript to the existing text
      if (final) {
        setFinalTranscript(prev => prev + " " + final);
      }
      // Update the interim transcript state for real-time feedback
      setInterimTranscript(interim);
    };

    recognition.onerror = (event) => {
      console.error("Speech recognition error:", event.error);
    };
  }, []);

  // Auto-scroll the text container to the bottom whenever the transcript updates
  useEffect(() => {
    if (containerRef.current) {
      containerRef.current.scrollTop = containerRef.current.scrollHeight;
    }
  }, [finalTranscript, interimTranscript]);

  // Function to toggle the speech recognition (recording) on and off
  const toggleRecording = () => {
    if (isRecording) {
      recognition.stop();
    } else {
      // Clear any previous interim results when starting a new recording session
      setInterimTranscript("");
      recognition.start();
    }
    setIsRecording(!isRecording);
  };

  // Render the UI: a record button and a scrollable container for the transcript text
  return (
    <div className="App">
      <button className="mic-button" onClick={toggleRecording}>
        {isRecording ? "Stop" : "Record"}
      </button>
      <div className="text-container" ref={containerRef}>
        <p className="transcript">
          {finalTranscript} <span className="interim">{interimTranscript}</span>
        </p>
      </div>
    </div>
  );
}

export default App;
