import React, { useState, useEffect, useRef } from "react";
import "./App.css";

// Helper function to determine if a sentence should be considered a question.
function isQuestionSentence(sentence) {
  sentence = sentence.trim();
  if (sentence.length === 0) return false;

  // Split the sentence into individual words.
  const words = sentence.split(/\s+/);
  const firstWord = words[0].toLowerCase();

  // Words that indicate a question only if they appear at the beginning.
  const startOnlyIndicators = [
    "is", "are", "am", "was", "were", "do", "does", "did",
    "can", "could", "would", "should", "will", "shall"
  ];
  // Words that may indicate a question regardless of position.
  const generalIndicators = ["what", "why", "how", "when", "where", "who"];

  // Check the first word.
  if (startOnlyIndicators.includes(firstWord) || generalIndicators.includes(firstWord)) {
    return true;
  }

  // For longer sentences (at least 4 words), also check the second word for start-only indicators.
  if (words.length >= 4) {
    const secondWord = words[1].toLowerCase();
    if (startOnlyIndicators.includes(secondWord)) {
      return true;
    }
  }

  // Check the rest of the sentence for any general question indicators.
  for (let word of words.slice(1)) {
    if (generalIndicators.includes(word.toLowerCase())) {
      return true;
    }
  }

  return false;
}

// Helper function to format a sentence: trim, capitalize, and add punctuation.
function formatSentence(sentence) {
  sentence = sentence.trim();
  if (sentence.length === 0) return "";

  const isQuestion = isQuestionSentence(sentence);
  // Capitalize first letter.
  sentence = sentence.charAt(0).toUpperCase() + sentence.slice(1);
  // Append punctuation if none exists.
  if (!/[.!?]$/.test(sentence)) {
    sentence += isQuestion ? "?" : ".";
  }

  return sentence;
}

// Set up Web Speech API for speech recognition.
const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
const recognition = new SpeechRecognition();
recognition.continuous = true;
recognition.interimResults = true;
recognition.lang = "en-US";

function App() {
  // States for recording and transcript.
  const [isRecording, setIsRecording] = useState(false);
  const [finalTranscript, setFinalTranscript] = useState("");
  const [interimTranscript, setInterimTranscript] = useState("");

  // States for connection and message status.
  const [connectionStatus, setConnectionStatus] = useState("Disconnected");
  const [messageStatus, setMessageStatus] = useState("");

  // Ref for auto-scroll.
  const containerRef = useRef(null);

  // State for WebSocket instance.
  const [socket, setSocket] = useState(null);

  // Establish and manage the WebSocket connection.
  useEffect(() => {
    let ws = null;
    try {
      // Replace with your actual WebSocket URL.
      ws = new WebSocket("ws://localhost:8080");
      ws.onopen = () => {
        console.log("Connected to WebSocket server");
        setConnectionStatus("Connected");
      };
      ws.onerror = (error) => {
        console.error("WebSocket error:", error);
        setConnectionStatus("Disconnected");
        setSocket(null);
      };
      setSocket(ws);
    } catch (error) {
      console.error("WebSocket connection error:", error);
      setConnectionStatus("Disconnected");
      setSocket(null);
    }
    return () => {
      if (ws) ws.close();
    };
  }, []);

  // Set up speech recognition event handlers.
  useEffect(() => {
    recognition.onresult = (event) => {
      let final = "";
      let interim = "";
      for (let i = event.resultIndex; i < event.results.length; i++) {
        const transcript = event.results[i][0].transcript;
        if (event.results[i].isFinal) {
          final += transcript;
        } else {
          interim += transcript;
        }
      }
      if (final) {
        const formattedFinal = formatSentence(final);
        setFinalTranscript(prev => prev + " " + formattedFinal);
      }
      setInterimTranscript(interim);
    };

    recognition.onerror = (event) => {
      console.error("Speech recognition error:", event.error);
    };

    recognition.onend = () => {
      console.log("Speech recognition ended.");
      if (isRecording) {
        console.log("Restarting speech recognition...");
        recognition.start();
      }
    };
  }, [isRecording]);

  // Periodically refresh the recognition session before the browser timeout.
  useEffect(() => {
    if (!isRecording) return;
    const refreshInterval = 50000; // 50 seconds
    const intervalID = setInterval(() => {
      console.log("Refreshing recognition session...");
      recognition.stop();
    }, refreshInterval);
    return () => clearInterval(intervalID);
  }, [isRecording]);

  // Smoothly auto-scroll the transcript container.
  useEffect(() => {
    if (containerRef.current) {
      containerRef.current.scrollTo({
        top: containerRef.current.scrollHeight,
        behavior: "smooth",
      });
    }
  }, [finalTranscript, interimTranscript]);

  // Send finalized transcript via WebSocket when updated.
  useEffect(() => {
    if (socket && socket.readyState === WebSocket.OPEN && finalTranscript.trim() !== "") {
      try {
        const message = JSON.stringify({ finalTranscript });
        socket.send(message);
        console.log("Sent finalized transcript:", message);
        // Indicate message sent for a brief moment.
        setMessageStatus("Message sent");
        setTimeout(() => setMessageStatus(""), 1000);
      } catch (err) {
        console.error("Failed to send message:", err);
      }
    } else {
      console.warn("WebSocket not available. Running without sending transcript.");
    }
  }, [finalTranscript, socket]);

  // Toggle recording on/off.
  const toggleRecording = () => {
    if (isRecording) {
      recognition.stop();
    } else {
      setInterimTranscript("");
      recognition.start();
    }
    setIsRecording(!isRecording);
  };

  return (
    <div className="App">
      <div className="controls">
        <button className="mic-button" onClick={toggleRecording}>
          {isRecording ? "Stop" : "Record"}
        </button>
        <span className="status">
          {connectionStatus} {messageStatus && ` - ${messageStatus}`}
        </span>
      </div>
      <div className="text-container" ref={containerRef}>
        <p className="transcript">
          {finalTranscript} <span className="interim">{interimTranscript}</span>
        </p>
      </div>
    </div>
  );
}

export default App;
