import React, { useState, useEffect, useRef } from "react";
import "./App.css";

// Helper function that determines if a sentence should be treated as a question.
// It only considers certain words (startOnlyIndicators) at the start of the sentence,
// while others (generalIndicators) are checked anywhere in the sentence.
function isQuestionSentence(sentence) {
  sentence = sentence.trim();
  if (sentence.length === 0) return false;

  // Split the sentence into words.
  const words = sentence.split(/\s+/);
  const firstWord = words[0].toLowerCase();

  // Words that only indicate a question if they appear at the beginning.
  const startOnlyIndicators = ["is", "are", "am", "was", "were", "do", "does", "did", "can", "could", "would", "should", "will", "shall"];
  // Words that can indicate a question anywhere in the sentence.
  const generalIndicators = ["what", "why", "how", "when", "where", "who"];

  // Check if the first word is one of the recognized question starters.
  if (startOnlyIndicators.includes(firstWord) || generalIndicators.includes(firstWord)) {
    return true;
  }

  // For additional detection, check if any general indicator appears elsewhere.
  for (let word of words.slice(1)) {
    if (generalIndicators.includes(word.toLowerCase())) {
      return true;
    }
  }

  return false;
}

// Helper function to format a sentence with proper capitalization and punctuation.
function formatSentence(sentence) {
  sentence = sentence.trim();
  if (sentence.length === 0) return "";

  // Determine if the sentence should be considered a question.
  const isQuestion = isQuestionSentence(sentence);

  // Capitalize the first letter.
  sentence = sentence.charAt(0).toUpperCase() + sentence.slice(1);

  // Append appropriate punctuation if the sentence does not already end in punctuation.
  if (!/[.!?]$/.test(sentence)) {
    sentence += isQuestion ? "?" : ".";
  }

  return sentence;
}

// Set up the Web Speech API for speech recognition.
const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
const recognition = new SpeechRecognition();
recognition.continuous = true;
recognition.interimResults = true;
recognition.lang = "en-US";

function App() {
  // State variables for tracking recording status and transcript data.
  const [isRecording, setIsRecording] = useState(false);
  const [finalTranscript, setFinalTranscript] = useState("");
  const [interimTranscript, setInterimTranscript] = useState("");

  // Reference to the scrollable text container (for auto-scrolling).
  const containerRef = useRef(null);

  // State to hold the WebSocket instance (null if unavailable).
  const [socket, setSocket] = useState(null);

  // Set up and manage the WebSocket connection with graceful fallback.
  useEffect(() => {
    let ws = null;
    try {
      // Replace with your actual WebSocket server URL (e.g., "ws://localhost:8080")
      ws = new WebSocket("ws://yourserveraddress:port");
      ws.onopen = () => {
        console.log("Connected to WebSocket server");
      };
      ws.onerror = (error) => {
        console.error("WebSocket error:", error);
        setSocket(null);
      };
      setSocket(ws);
    } catch (error) {
      console.error("WebSocket connection error:", error);
      setSocket(null);
    }
    // Clean up on component unmount.
    return () => {
      if (ws) ws.close();
    };
  }, []);

  // Set up speech recognition event handlers.
  useEffect(() => {
    recognition.onresult = (event) => {
      let final = "";
      let interim = "";
      // Process each result from the speech recognition engine.
      for (let i = event.resultIndex; i < event.results.length; i++) {
        const transcript = event.results[i][0].transcript;
        if (event.results[i].isFinal) {
          final += transcript;
        } else {
          interim += transcript;
        }
      }
      // If finalized text exists, format it and append to the current transcript.
      if (final) {
        const formattedFinal = formatSentence(final);
        setFinalTranscript(prev => prev + " " + formattedFinal);
      }
      // Update the interim transcript (for real-time feedback).
      setInterimTranscript(interim);
    };

    recognition.onerror = (event) => {
      console.error("Speech recognition error:", event.error);
    };

    // Restart recognition when it ends if recording is still active.
    recognition.onend = () => {
      console.log("Speech recognition ended.");
      if (isRecording) {
        console.log("Restarting speech recognition...");
        recognition.start();
      }
    };
  }, [isRecording]);

  // Periodically refresh the recognition session before the browser's time limit is reached.
  useEffect(() => {
    if (!isRecording) return;
    // Refresh interval is set to 50 seconds (adjust as needed).
    const refreshInterval = 50000; // milliseconds
    const intervalID = setInterval(() => {
      console.log("Refreshing recognition session...");
      recognition.stop();
    }, refreshInterval);
    return () => clearInterval(intervalID);
  }, [isRecording]);

  // Smoothly auto-scroll the text container to display the most recent transcript.
  useEffect(() => {
    if (containerRef.current) {
      containerRef.current.scrollTo({
        top: containerRef.current.scrollHeight,
        behavior: "smooth",
      });
    }
  }, [finalTranscript, interimTranscript]);

  // Send only the finalized transcript via WebSocket when updated.
  useEffect(() => {
    if (socket && socket.readyState === WebSocket.OPEN) {
      try {
        const message = JSON.stringify({ finalTranscript });
        socket.send(message);
        console.log("Sent finalized transcript:", message);
      } catch (err) {
        console.error("Failed to send message:", err);
      }
    } else {
      console.warn("WebSocket not available. Running without sending transcript.");
    }
  }, [finalTranscript, socket]);

  // Toggle the recording state: start or stop the speech recognition.
  const toggleRecording = () => {
    if (isRecording) {
      recognition.stop();
    } else {
      // Clear interim results before starting a new session.
      setInterimTranscript("");
      recognition.start();
    }
    setIsRecording(!isRecording);
  };

  // Render the UI.
  return (
    <div className="App">
      <button className="mic-button" onClick={toggleRecording}>
        {isRecording ? "Stop" : "Record"}
      </button>
      <div className="text-container" ref={containerRef}>
        <p className="transcript">
          {finalTranscript} <span className="interim">{interimTranscript}</span>
        </p>
      </div>
    </div>
  );
}

export default App;
